
public class CountString_Nishchay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String word= "Weattach";
		int count=0;
		int len = word.length();
		for(int i=1;i<= len;i++)
		{
			count++;
		}
		
		System.out.println("Total number of characters in Weattach=" + count );

	}

}
